import datetime
import os

class Database:
    def __init__(self, filename):
        script_dir = os.path.dirname(os.path.abspath(__file__))
        self.filename = os.path.join(script_dir, filename)
        self.users = None
        self.file = None
        self.load()
        
    def load(self):
        self.file=open(self.filename, 'r')
        self.users = {}
        
        for line in self.file:
            email, password, name, created = line.strip().split(';')
            self.users[email]=(password, name, created)
            
        self.file.close()
        
    def getUser(self, email):
        if email in self.users:
            return self.users[email]
        else:
            return -1
    
    def addUser(self, email, password, name):
        if email.strip() not in self.users:
            self.users[email.strip()] = (password.strip(), name.strip(), Database.getDate())
            self.save()
            return 1
        else:
            print('E-mail j√° existente.')
            return -1
    
    def validate(self, email, password):
        if self.getUser(email) != -1:
            return self.users[email][0] == password
        else:
            return False
    
    def save(self):
        with open(self.filename, 'w') as f:
            for user in self.users:
                f.write(f"{user};{self.users[user][0]};{self.users[user][1]};{self.users[user][2]}\n")
                
    @staticmethod
    def getDate():
        return f"{datetime.datetime.now().strftime('%d/%m/%Y')}"